import React from "react";
import { Typography } from "@mui/material";
import { Divider } from "@mui/material";
import Counter from "./Counter";
import { useDispatch } from "react-redux";
import {
  incrementCount,
  decrementCount,
} from "../pages/orderSlice";

export const CheckoutItem = ({ id, title, price, count, isMeal }) => {
  const dispatch = useDispatch();
  const [currentCount, setCurrentCount] = React.useState(count);
  const handleAddButtonClick = React.useCallback(
    (id) => dispatch(isMeal ? incrementMealCount(id) : incrementDrinkCount(id)),
    [dispatch, isMeal]
  );

  const handleRemoveButtonClick = React.useCallback(
    (id) => dispatch(isMeal ? decrementMealCount(id) : decrementDrinkCount(id)),
    [dispatch, isMeal]
  );
  return (
    <div style={{ marginBlock: 20 }}>
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
          alignItems: "center",
          marginInline: 20,
        }}
      >
        <div>
          <Typography
            component="div"
            variant="h5"
            //style={{ marginBottom: 5 }}
          >
            {title}
          </Typography>
          <Typography
            component="div"
            variant="h5"
            sx={{
              //fontSize: 12,
              marginTop: 0.5,
            }}
          >
            {price} â‚¬
          </Typography>
        </div>
        <Counter
          id={id}
          count={count}
          onAddButtonClick={handleAddButtonClick}
          onRemoveButtonClick={handleRemoveButtonClick}
        />
      </div>
      <Divider variant="middle" />
    </div>
  );
};
