import React from "react";
import MenuItem3 from "../components/MenuItem3";
import { useDispatch, useSelector } from "react-redux";
import Card from "@mui/material/Card";
import Box from "@mui/material/Box";
import CardContent from "@mui/material/CardContent";
import CardMedia from "@mui/material/CardMedia";
import IconButton from "@mui/material/IconButton";
import Typography from "@mui/material/Typography";
import { addToCart } from "./drinksSlice";
import { useMealsQuery } from "../generated/graphql";
import burgerUrl from '../static/'

const FoodMenuPage = () => {
  const { data, error, loading } = useMealsQuery();
  const [mealOrder, setMealOrder] = React.useState({});
  const dispatch = useDispatch();

  const handleAddButtonClick = React.useCallback(
    (id) =>
      setMealOrder({
        ...mealOrder,
        [id]: ++mealOrder[id],
      }),
    [mealOrder]
  );

  const handleRemoveButtonClick = React.useCallback(
    (id) => {
      if (mealOrder[id] === 0) {
        return;
      }
      setMealOrder({
        ...mealOrder,
        [id]: --mealOrder[id],
      });
    },
    [mealOrder]
  );

  //TODO ADD some kind of user notification that items were added into cart
  const handleAddToBag = React.useCallback(
    (id, name, price) =>
      dispatch(addToCart({ id, name, count: mealOrder[id], price })),
    [mealOrder, dispatch]
  );
  return (
    <div className="drinks-menu-page-wrapper">
      {/* {data?.meals.map(({ id, name, price }) => (
        <MenuItem3
          key={id}
          id={id}
          onAddButtonClick={handleAddButtonClick}
          onRemoveButtonClick={handleRemoveButtonClick}
          onAddToBagClick={handleAddToBag}
          name={name}
          count={mealOrder.cocaCola}
          price={price}
        />
      ))} */}
      <Card sx={{ display: "flex" }}>
        <Box>
          <CardContent>
            <Typography component="div" variant="h5">
              Kralovsky burger
            </Typography>
            <Typography
              variant="subtitle1"
              color="text.secondary"
              component="div"
            >
              100 % hovadzie maso, domaca slaninka, vajicko, majoneza
            </Typography>
          </CardContent>
        </Box>
        <CardMedia
          style={{ height: "250px", paddingTop: "2%" }}
          component="img"
          sx={{ width: 151, height: 120 }}
          //image={require("../static/burger.jpeg")}
          //alt="Live from space album cover"
        />
      </Card>
    </div>
  );
};

export default FoodMenuPage;
