import React from "react";
import { Typography } from "@mui/material";
import { Divider } from "@mui/material";

export const PriceTaggedItem = ({ title, price, additionalOrderInfo }) => {
  return (
    <div
      style={{
        height: 40,
        marginBlock: 15,
      }}
    >
      <div
        style={{
          display: "flex",
          flexDirection: "row",
          justifyContent: "space-between",
          marginInline: 20,
        }}
      >
        <div>
          <Typography
            component="div"
            variant="h5"
            //style={{ marginBottom: 5 }}
          >
            {title}
          </Typography>
          {additionalOrderInfo && (
            <Typography
              component="div"
              variant="body1"
              sx={{
                fontSize: 14,
                marginLeft: 1,
                marginTop: 0.5,
                width: "600%",
              }}
            >
              + mlieko odobrat hranolky posolit viac
            </Typography>
          )}
        </div>
        <Typography
          component="div"
          variant="h5"
          //style={{ marginBottom: 5 }}
        >
          {price}
        </Typography>
      </div>
      <Divider variant="middle" />
    </div>
  );
};
